import java.util.Scanner;
public class 문제1 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a;
		int b;
		a=3;
		b=a+5;
		System.out.printf("a의 값: %d \n",a);
		System.out.printf("b의 값: %d \n",b);
		
	}
	
	// 정리 
	// printf와  println 차이 => 서식 사용 유무 , 자동으로 한줄 띄어쓰기 
	// %d : 정수 , %f : 실수 
}
